mapper.select:
        SELECT


--         级联表的字段

        FROM checi  a



                and a.id in

                #{item}



                and a.checi_name like CONCAT('%',#{params.checiName},'%')


                and a.checi_types = #{params.checiTypes}


                  and a.checi_new_money >= #{params.checiNewMoneyStart}   


                  and a.checi_new_money <= #{params.checiNewMoneyEnd}   


                and a.checi_chufadi like CONCAT('%',#{params.checiChufadi},'%')


                and a.checi_mudidi like CONCAT('%',#{params.checiMudidi},'%')


                  and UNIX_TIMESTAMP(a.checi_time) >= UNIX_TIMESTAMP(#{params.checiTimeStart}) 


                  and UNIX_TIMESTAMP(a.checi_time) <= UNIX_TIMESTAMP(#{params.checiTimeEnd}) 


                  and a.section_number >= #{params.sectionNumberStart}   


                  and a.section_number <= #{params.sectionNumberEnd}   


                and a.section_number = #{params.sectionNumber}


                  and a.zuowei_number >= #{params.zuoweiNumberStart}   


                  and a.zuowei_number <= #{params.zuoweiNumberEnd}   


                and a.zuowei_number = #{params.zuoweiNumber}


                and a.shangxia_types = #{params.shangxiaTypes}


                  and a.checi_delete >= #{params.checiDeleteStart}   


                  and a.checi_delete <= #{params.checiDeleteEnd}   


                and a.checi_delete = #{params.checiDelete}


                and a.checi_content like CONCAT('%',#{params.checiContent},'%')




        order by a.${params.orderBy} desc
